mapper.update:
UPDATE
    order_detail
SET
    state = #{state}
WHERE
    order_detail.order_number = #{orderNumber}

UPDATE
    order_detail
SET
    state = #{state}
WHERE
    order_detail.order_number = #{orderNumber}
    AND
    order_detail.od_id = #{odId}
    AND
    is_delete=0

mapper.select:
SELECT order_detail.total_amount
FROM
    order_detail
WHERE
    order_detail.order_number = #{orderNumber}
    AND
    order_detail.od_id = #{odId}
    AND
    order_detail.state = 0;

SELECT * FROM order_detail AS od JOIN `order` AS b ON od.order_number=b.order_number WHERE od.store_id=#{storeId} AND od.is_delete=0 AND od.state=#{type}
AND DATEDIFF(NOW(),b.order_date) <= #{params.startTime}
AND od.total_amount >= #{params.totalMoneyFloor}
AND od.total_amount <= #{params.totalMoneyCeil}
AND od.address_msg LIKE "%${params.address}%"
AND od.address_msg LIKE "%${params.takePhone}%"
AND od.logistics_no= #{params.logistics}
AND DATEDIFF(NOW(),od.over_date) <= #{params.endTime}
AND state=4
AND state=3
ORDER BY b.order_date
ORDER BY od.total_amount
ORDER BY od.over_date
