mapper.update:
UPDATE
    order_detail
SET
    state = #{state}
WHERE
    order_detail.order_number = #{orderNumber}

UPDATE
    order_detail
SET
    state = #{state}
WHERE
    order_detail.order_number = #{orderNumber}
    AND
    order_detail.od_id = #{odId}

mapper.select:
SELECT order_detail.total_amount
FROM
    order_detail
WHERE
    order_detail.order_number = #{orderNumber}
    AND
    order_detail.od_id = #{odId}
    AND
    order_detail.state = 0;
