mapper.update:
UPDATE
    commodity
SET
    status_code = #{state}
WHERE
    commodity_id = #{commodityId}

mapper.select:
SELECT co.commodity_id, co.commodity_name, co.commodity_describe, co.master_map, SUBSTRING_INDEX(c.price, ";", 1) AS price FROM commodity AS co JOIN ( SELECT cav.commodity_id, cav.price FROM cav WHERE cav.price IS NOT NULL ) AS c ON co.commodity_id = c.commodity_id
co.commodity_name LIKE '%'+${condition.keyWord}+'%' OR co.commodity_describe LIKE '%'+${condition.keyWord}+'%'
ORDER BY #{condition.sortName}
#{condition.sortRule}
LIMIT #{condition.page},#{condition.pageNum};

SELECT commodity_name FROM commodity WHERE (
id
)
